import React, { Component } from 'react';
import axios from 'axios';

class App extends Component {
  state = {
    articles: [],
  }

  componentDidMount = () => {
    axios.get('http://localhost:3012/')
      .then(res => {
        this.setState({ articles: res.data });
        console.log(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }

  render() {
    return (
      <div className="App">
        {this.state.articles.map((article) =>
          <div key={article._id}>
            {article.title}
            {article.text}
          </div>
        )}
      </div>
    );
  }
}

export default App;
