import React, { Component } from 'react';
import axios from 'axios';
import Form from './Form';
import SingleNote from './SingleNote';

class Notes extends Component {
	state = {
		articles: [],
	}

	componentDidMount = () => {
		axios.get('http://localhost:3012/')
			.then(res => {
				this.setState({ articles: res.data });
				console.log(res.data);
			})
			.catch((err) => {
				console.log(err);
			});
	}

	getArticleById = (id) =>{
		axios.get('http://localhost:3012/'+id)
		.then(res => {
			console.log(res.data);
		})
		.catch((err) => {
			console.log(err);
		});
	}

	updateArticles = (value) => {
		this.setState({ newNote: value })
		this.setState({ articles: [...this.state.articles, this.state.newNote] });
	}

	render() {
		return (
			<div>
				<Form updateArticles={this.updateArticles}/>
				<SingleNote articles={this.state.articles}/>
			</div>
		);
	}
}

export default Notes;
