import React, { Component } from 'react';
import axios from 'axios';
import { stringify } from 'querystring';

class Form extends Component {
	state = {
		articles: [],
		title: '',
		text: ''
	}

	handleChangeTitle = (event) => {
		this.setState({ title: event.target.value });
	}

	handleChangeText = (event) => {
		this.setState({ text: event.target.value });
	}

	updateArticles = (event) => {
		event.preventDefault();

		const newArticle = {
			title: this.state.title,
			text: this.state.text
		};


		axios.post('http://localhost:3012/', stringify(newArticle))
			.then(res => {
				this.props.updateArticles(res.data)
			})
	}

	render() {
		return (
			<form onSubmit={this.updateArticles}>
				<input type="text" name="title" onChange={this.handleChangeTitle} />
				<input type="text" name="text" onChange={this.handleChangeText} />
				<button type="submit">ok</button>
			</form>
		)
	}
}

export default Form;
