import React, { Component } from 'react';
import axios from 'axios';
import Form from './Form';

class Notes extends Component {
	state = {
		articles: [],
	}

	componentDidMount = () => {
		axios.get('http://localhost:3012/')
			.then(res => {
				this.setState({ articles: res.data });
				console.log(res.data);
			})
			.catch((err) => {
				console.log(err);
			});
	}

	getArticleById = (value) => {
		console.log(value)
	}

	ArticlesMap = this.state.articles.map((article, index) => {
		return (
			<div
				data-id={index}
				key={article._id}
				onClick={this.getArticleById(article._id)}>
				<h2>{article.title}</h2>
				<p>{article.text}</p>
				<button>Удалить</button>
			</div>
		)
	})

	updateArticles = (value) => {
		this.setState({ newNote: value })
		this.setState({ articles: [...this.state.articles, this.state.newNote] });
	}

	render() {
		return (
			<div>
				<Form updateArticles={this.updateArticles} />
				{this.ArticlesMap}
			</div>
		);
	}
}

export default Notes;
