import React, { Component } from 'react';
import axios from 'axios';
import { stringify } from 'querystring';

class Form extends Component {
	state = {
		articles: [],
		title: '',
		text: ''
	}

	handleChangeTitle = event => {
		this.setState({ title: event.target.value });
	}

	handleChangeText = event => {
		this.setState({ text: event.target.value });
	}

	updateNotes = event => {
		event.preventDefault();

		const newArticle = {
			title: this.state.title,
			text: this.state.text
		};

		const config = {
			headers: {
				'Content-Type': 'application/x-www-form-urlencoded',
				'Accept': 'application/json'
			}
		};

		axios.post('http://localhost:3012/', stringify(newArticle), config)
			.then(res => {
				this.setState({ articles: [...this.state.articles, res.data] });
			})
	}

	render() {
		return (
			<form onSubmit={this.updateNotes}>
				<input type="text" name="title" onChange={this.handleChangeTitle} />
				<input type="text" name="text" onChange={this.handleChangeText} />
				<button type="submit">ok</button>
			</form>
		)
	}
}

export default Form;
