import React, { Component } from 'react';
import axios from 'axios';
import { stringify } from 'querystring';
import styled from 'styled-components'

const FormBlock = styled.div`
	position: absolute;
	top: 50%;
	left: 50%;
	transform: translate(-50%, -50%);
	width: 700px;
	text-align: center;
`

const FormInput = styled.input`
	width: 80%;
	height: 300px;
	font-size: 24px;
	outline: none;
	margin-top: 20px;
`

const FormArea = styled.input`
	width: 80%;
	font-size: 24px;
	outline: none;
	margin-top: 20px;
`

const FormSubmit = styled.button`
	display: block;
`

class Form extends Component {
	state = {
		articles: [],
		title: '',
		text: ''
	}

	handleChangeTitle = (event) => {
		this.setState({ title: event.target.value });
	}

	handleChangeText = (event) => {
		this.setState({ text: event.target.value });
	}

	addArticle = (event) => {
		event.preventDefault();

		const newArticle = {
			title: this.state.title,
			text: this.state.text
		};

		axios.post('http://localhost:3012/', stringify(newArticle))
			.then(res => {
				this.props.addArticle(res.data)
			})
	}

	render() {
		return (
			<FormBlock>
				<form onSubmit={this.addArticle}>
					<FormInput type="text" name="title" onChange={this.handleChangeTitle} />
					<FormArea type="text" name="text" onChange={this.handleChangeText} />
					<FormSubmit type="submit">ok</FormSubmit>
				</form>
			</FormBlock>
		)
	}
}

export default Form;
