import React, { Component } from 'react';
import styled from 'styled-components'

const NotePopupBlock = styled.div`
	visibility: ${props => props.fade ? 'hidden' : 'visible'};
	position: absolute;
	top: 50%;
	left: 50%;
	transform: translate(-50%, -50%);
	width: 300px;
	background-color: darkcyan;
`

class SingleNotePopup extends Component {
	state = {
		articleData: this.props.article,
		articleVisibility: this.props.articleVisibility,
	}

	componentWillReceiveProps = (nextProps) => {
		this.setState({ articleData: nextProps.article });
		this.setState({ articleVisibility: nextProps.articleVisibility });
	}

	hideNotePopup = () =>{
		this.setState({visible: !this.state.visible});
		this.setState({title: ''});
		this.setState({text: ''});
	}

	render(){
		return (
			<NotePopupBlock fade={!this.state.articleVisibility}>
				<Overlay onClick={this.hideNotePopup}/>
				<h1>{this.state.articleData.title}</h1>
				<p>{this.state.articleData.text}</p>
			</NotePopupBlock>
		)
	}
}

export default SingleNotePopup
