import React, { Component } from 'react';
import axios from 'axios';
import SingleArticlePopup from './SingleArticlePopup';
import styled from 'styled-components'

const lightGrey = '#9ea3b4';

const ArticlesSection = styled.section`
	display: flex;
	flex-direction: row;
`

const ArticleBlock = styled.div`
	background-color: #3a3e52;
	width: 350px;
	border-radius: 3px;
	margin: 10px 10px;
	padding: 15px;
	box-sizing: border-box;
	position: relative;
`

const Avatar = styled.div`
	display: inline-block;
	vertical-align: middle;
	width: 40px;
	height: 40px;
	border-radius: 50%;
	background-color: yellow;
	
`

const ArticleContent = styled.div`
	display: inline-block;
	vertical-align: middle;
	margin-left: 15px;
`

const ArticleTitle = styled.p`
	margin: 0;
	cursor: pointer;
`

const ArticleDate = styled.span`
	font-size: 12px;
	color: ${lightGrey};
	margin: 0;
`

const EditBtn = styled.div`
	position: absolute;
	top: 50%;
	transform: translateY(-50%);
	right: 10px;
	width: 10px;
	height: 35px;
	&:hover{
		cursor: pointer;
	}
`

const EditBtnSpan = styled.span`
	position: absolute;
	width: 5px;
	height: 5px;
	border-radius: 50%;
	background-color: ${lightGrey};
	&:after{
		content: '';
		position: absolute;
		top: 10px;
		right: 0;
		width: 5px;
		height: 5px;
		border-radius: 50%;
		background-color: ${lightGrey};
	}
	&:before{
		content: '';
		position: absolute;
		bottom: 10px;
		right: 0;
		width: 5px;
		height: 5px;
		border-radius: 50%;
		background-color: ${lightGrey};
	}
`

class Articles extends Component {
	state = {
		articles: this.props.articles,
		singleArticle: '',
		articleVisibility: false
	};

	componentWillReceiveProps = (nextProps) => {
		this.setState({ articles: nextProps.articles });
	}

	getArticleById = (id) => {
		axios.get('http://localhost:3012/' + id)
			.then(res => {
				this.setState({ singleArticle: res.data });
				this.setState({ articleVisibility: true });
			})
			.catch((err) => {
				console.log(err);
			});
	}

	deleteArticleById = (id, index) => {
		axios.delete('http://localhost:3012/' + id)
			.then(res => {
				const articlesArray = this.state.articles;
				articlesArray.splice(index, 1);
				this.setState({ articles: articlesArray });
			})
			.catch((err) => {
				console.log(err);
			});
	}

	render() {
		return (
			<ArticlesSection>
				{this.state.articles.map((article, index) =>
					<ArticleBlock key={article._id}>
						<div index={index}>
							<Avatar />
							<ArticleContent>
								<ArticleTitle onClick={this.getArticleById.bind(this, article._id)}>{article.title}</ArticleTitle>
								<ArticleDate>11.01.1222</ArticleDate>
							</ArticleContent>
						</div>
						<EditBtn>
							<EditBtnSpan />
						</EditBtn>
						<button
							onClick={this.deleteArticleById.bind(this, article._id, index)}>
							Удалить
						</button>
					</ArticleBlock>
				)}
				<SingleArticlePopup
					articleVisibility={this.state.articleVisibility}
					article={this.state.singleArticle} />
			</ArticlesSection>
		)
	}
}

export default Articles;
